mapper.select:
select c.table_gid tableGid, c.game_type gameType, FROM_UNIXTIME(c.begin_time) beginTime, FROM_UNIXTIME(c.end_time) endTime, c.side_list sideList,c.uid, c.total_num totalNum,c.ai_num aiNum, c.add_score addScore,c.pay_fee payFee,c.bet_coins betCoins from data_card c
and c.table_gid = #{tableGid}
and c.mstime between #{beginms} and #{endms}
and c.game_type = #{gameId}

select u.table_gid tableGid, u.game_type gameType, FROM_UNIXTIME(u.begin_time) beginTime, FROM_UNIXTIME(u.end_time) endTime, c.total_num totalNum,u.add_score addScore,u.pay_fee payFee,u.bet_coins betCoins, c.ai_num aiNum,c.side_list sideList from data_card_user u left join data_card c on u.table_gid = c.table_gid
and u.table_gid = #{tableGid}
and u.mstime between #{beginms} and #{endms}
and u.game_type = #{gameId}

select sum(c.add_score) countScore,sum(c.pay_fee) countPayfee,sum(c.bet_coins) countBetCoins
from data_card c


        and c.table_gid = #{tableGid}


        and c.mstime between #{beginms} and #{endms}


        and c.game_type = #{gameId}

select sum(u.add_score) countScore,sum(u.pay_fee) countPayfee,sum(u.bet_coins) countBetCoins
from data_card_user u


        and u.table_gid = #{tableGid}


        and u.mstime between #{beginms} and #{endms}


        and u.game_type = #{gameId}
