mapper.select:
select count(1) from data_register
where uid = #{uid}

    and channel in (select t_id from sys_tenant where t_type = '2' and ancestors like CONCAT('%', #{tenant}, '%'))

mapper.update:
update control_player_config
type = #{type}, risk_power = #{riskPower}, send_level = #{sendLevel}, mark = #{mark}, create_by = #{createBy}, create_time = sysdate()
where uid = #{uid}
