mapper.select:
select IFNULL(sum(recharge_amount),0) amount from config_recharge_order
where payment_status = 1

    and uid = #{uid}


    and finish_time between #{beginTime} and #{endTime}


    and channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')


   and channel = #{channelId}

select IFNULL(sum(recharge_amount),0) amount from config_recharge_order where payment_status = 1
and uid = #{uid}
and channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')

select IFNULL(sum(withdraw_money),0) amount  from config_exchange_order
where exaa_status = 5 and uid = #{uid}

    and update_time between #{beginms} and #{endms}


    and channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')

select IFNULL(sum(withdraw_money),0) amount  from config_exchange_order
where exaa_status = 5 and uid = #{uid}

    and channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')

SELECT cg.game_name gameNames FROM data_card dc LEFT JOIN config_game cg ON dc.table_type = cg.game_id WHERE cg.game_name is not null and uid = #{uid}
and dc.mstime between #{beginms} and #{endms}
GROUP BY cg.game_name ORDER BY sum(dc.system_win) desc LIMIT 3

SELECT ceo.uid,IFNULL(SUM(ceo.withdraw_money),0) withdrawAmount from config_exchange_order ceo WHERE ceo.exaa_status in(1,2,3,4,5,6)
and channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')
and channel IN ( SELECT t.t_id FROM sys_tenant t WHERE t.t_type = '2' AND (t.ancestors LIKE concat('%', #{currentUserId}, '%') OR t_id = #{currentUserId}) )
and ceo.update_time >= #{beginTime}
and ceo.update_time <= #{endTime}
GROUP BY ceo.uid ORDER BY withdrawAmount desc LIMIT 100

SELECT IFNULL(sum(dw.value),0) betTotal from data_water dw
WHERE uid = #{uid}

    and channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')


    and dw.time >=  #{beginTime}


    and dw.time  <=  #{endTime}

SELECT sum(case when recharge_amount>0 and recharge_amount < 50 then 1 else 0 end) as count1, sum(case when recharge_amount>=50 and recharge_amount < 100 then 1 else 0 end) as count2, sum(case when recharge_amount>=100 and recharge_amount < 200 then 1 else 0 end) as count3, sum(case when recharge_amount>=200 and recharge_amount < 500 then 1 else 0 end) as count4, sum(case when recharge_amount>=500 and recharge_amount < 1000 then 1 else 0 end) as count5, sum(case when recharge_amount>=1000 and recharge_amount < 3000 then 1 else 0 end) as count6, sum(case when recharge_amount>=3000 and recharge_amount < 5000 then 1 else 0 end) as count7, sum(case when recharge_amount>=5000 and recharge_amount < 10000 then 1 else 0 end) as count8, sum(case when recharge_amount>=10000 and recharge_amount < 20000 then 1 else 0 end) as count9, sum(case when recharge_amount>=20000 and recharge_amount < 50000 then 1 else 0 end) as count10, sum(case when recharge_amount>=50000 and recharge_amount < 100000 then 1 else 0 end) as count11, sum(case when recharge_amount>=100000 then 1 else 0 end) as count12 from config_recharge_order where payment_status = 1
and channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')
and ac.channel IN ( SELECT t.t_id FROM sys_tenant t WHERE t.t_type = '2' AND (t.ancestors LIKE concat('%', #{currentUserId}, '%') OR t_id = #{currentUserId}))
and finish_time between #{beginTime} and #{endTime}
and channel = #{channelId}

SELECT
    count(DISTINCT(dcu.uid))
from data_card_user dcu
LEFT JOIN agent_commission ac ON dcu.uid = ac.uid


        and dcu.channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')


        and dcu.channel = #{channelId}


        and ac.agent_id = #{agentId}


        and dcu.mstime between #{beginms} and #{endms}


        and (dcu.uid = #{keyword} or dcu.uid = (select uid from data_register where phone=  #{keyword}))


        and dcu.table_type = #{gameId}

SELECT dcu.uid, ac.agent_id agentId, dcu.channel, count(dcu.uid) gameTableNum, sum(dcu.bet_coins) betCoins, sum(dcu.add_score) addScore, (sum(dcu.add_score) + sum(dcu.bet_coins)) rewardAmount from data_card_user dcu LEFT JOIN agent_commission ac ON dcu.uid = ac.uid
and dcu.channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')
and dcu.channel = #{channelId}
and ac.agent_id = #{agentId}
and dcu.mstime between #{beginms} and #{endms}
and (dcu.uid = #{keyword} or dcu.uid = (select uid from data_register where phone= #{keyword}))
and dcu.table_type = #{gameId}
GROUP BY dcu.channel,dcu.uid ORDER BY dcu.channel,
sum(dcu.bet_coins)
(sum(dcu.add_score) + sum(dcu.bet_coins))
sum(dcu.add_score)
DESC

SELECT cg.game_name gameName, count(dcu.uid) gameTableNum, sum(dcu.bet_coins) betCoins, (sum(dcu.add_score) + sum(dcu.bet_coins)) rewardAmount, sum(dcu.add_score) addScore from data_card_user dcu LEFT JOIN config_game cg ON dcu.table_type = cg.game_id
and dcu.channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')
and dcu.channel = #{channelId}
and ac.agent_id = #{agentId}
and dcu.mstime between #{beginms} and #{endms}
and (dcu.uid = #{keyword} or dcu.uid = (select uid from data_register where phone= #{keyword}))
and dcu.table_type = #{gameId}
GROUP BY cg.game_name ORDER BY sum(dcu.add_score) DESC

SELECT FROM_UNIXTIME(dcu.begin_time, '%Y-%m-%d') as day, dcu.channel, dcu.uid, ac.agent_id agentId, count(dcu.uid) gameTableNum, sum(dcu.bet_coins) betCoins, (sum(dcu.add_score) + sum(dcu.bet_coins)) rewardAmount, sum(dcu.add_score) addScore from data_card_user dcu LEFT JOIN agent_commission ac on dcu.uid = ac.uid
and dcu.channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')
and dcu.channel = #{channelId}
and ac.agent_id = #{agentId}
and dcu.mstime between #{beginms} and #{endms}
and (dcu.uid = #{keyword} or dcu.uid = (select uid from data_register where phone= #{keyword}))
and dcu.table_type = #{gameId}
GROUP BY dcu.channel,dcu.uid,day ORDER BY day DESC
