mapper.select:
SELECT dod.create_time as day, COUNT(distinct uid) as userCount from data_online_day dod where dod.create_time >= DATE_SUB(now(), interval 1 DAY) and dod.create_time <= now()
and dod.channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')
and dod.channel = #{channel}
and dod.table_type = #{gameId}
group by dod.create_time

SELECT round(avg(t.userCount), 0) as avgNum from ( SELECT dod.create_time as day, COUNT(distinct uid) as userCount from data_online_day dod where dod.create_time >= DATE_SUB(now(), interval 1 DAY) and dod.create_time <= now()
and dod.channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')
and dod.channel = #{channel}
and dod.table_type = #{gameId}
group by dod.create_time )t

SELECT t.day as day, MAX(t.userCount) as userCount from ( SELECT dod.day, dod.create_time, COUNT(distinct uid) as userCount from data_online_day dod where 1 = 1
and dod.channel IN (SELECT t_id FROM sys_tenant WHERE tenant = #{tid} AND t_type = '2')
and dod.channel = #{channel}
and dod.table_type = #{gameId}
group by dod.day, dod.create_time ) t where 1 = 1
and t.day = #{day}
group by day
