mapper.select:
SELECT tid as tId, game_id AS gameId, parent_id AS parentId, game_name AS gameName, game_type AS gameType, status AS status, game_bs AS gameBs, user_sort AS userSort FROM config_game where parent_id = "0"
and game_id = #{gameId}
and game_type = #{gameType}
and tid = #{tId}

mapper.update:
update config_game
status = #{status}, game_type = #{gameType}, game_bs = #{gameBs}, user_sort = #{userSort}, updated_time = sysdate()
where game_id = #{gameId}
