mapper.update:
update config_update
tid = #{tid}, status = #{status}, version = #{version}, ver_int = #{verInt}, apk_update_url = #{apkUpdateUrl}, size = #{size}, update_time = sysdate()
where id = #{id}

update config_package
allow_version = #{allowVersion}, deny_version = #{denyVersion}, allow_channel = #{allowChannel}, deny_channel = #{denyChannel}, is_public = #{isPublic}, status = #{status}, platform = #{platform}, release_time = #{releaseTime}, version = #{version}, game_info = #{gameInfo}, apk_update_url = #{apkUpdateUrl}, `size` = #{size}, update_time = sysdate()
where id = #{id}
