mapper.insert:
insert into agent_commission(uid,agent_id,channel,team_num,sub_num,sub_ratio,sub_income,other_num,other_ratio,other_income,total_income,cash_income,wait_income,history_income) values
(#{item.uid},#{item.agentId},#{item.channel},#{item.teamNum},#{item.subNum},#{item.subRatio},#{item.subIncome} ,#{item.otherNum},#{item.otherRatio},#{item.otherIncome},#{item.totalIncome},#{item.cashIncome},#{item.waitIncome},#{item.totalIncome})
ON DUPLICATE KEY UPDATE agent_id=values(agent_id), channel=values(channel), team_num=values(team_num), sub_num=values(sub_num), sub_ratio=sub_ratio+values(sub_ratio), sub_income=sub_income+values(sub_income), other_num=other_num+values(other_num), other_ratio=other_ratio+values(other_ratio), other_income=other_income+values(other_income), total_income=total_income+values(total_income), cash_income=cash_income+values(cash_income), wait_income=wait_income+values(wait_income), history_income=history_income+values(history_income);

insert into agent_commission_day(day,uid,agent_id,channel,team_num,sub_num,sub_ratio,sub_income,other_num,other_ratio,other_income,total_income,cash_income,wait_income) values
(#{item.day},#{item.uid},#{item.agentId},#{item.channel},#{item.teamNum},#{item.subNum},#{item.subRatio},#{item.subIncome} ,#{item.otherNum},#{item.otherRatio},#{item.otherIncome},#{item.totalIncome},0,#{item.totalIncome})
ON DUPLICATE KEY UPDATE agent_id=values(agent_id), channel=values(channel), team_num=values(team_num), sub_num=values(sub_num), sub_ratio=values(sub_ratio), sub_income=values(sub_income), other_num=values(other_num), other_ratio=values(other_ratio), other_income=values(other_income), total_income=values(total_income), wait_income=values(total_income)-cash_income;

insert into agent_popularize_day(day,uid,team_num,sub_num,other_num) values
(#{item.day},#{item.uid},#{item.teamNum},#{item.subNum},#{item.otherNum})
ON DUPLICATE KEY team_num=values(team_num), sub_num=values(sub_num), other_num=values(other_num);
