mapper.select:
select oper_id, title, business_type, method, request_method, operator_type, oper_name, oper_url, oper_ip, oper_location, oper_param, json_result, l.status, error_msg, oper_time from sys_oper_log l left join sys_user u on l.oper_name = u.user_name
and l.tenant = 0
AND l.title like concat('%', #{title}, '%')
AND l.business_type = #{businessType}
AND l.business_type in
#{businessType}
AND l.status = #{status}
AND l.oper_name like concat('%', #{operName}, '%')
and date_format(l.oper_time,'%Y-%m-%d %H:%i-%s') >= date_format(#{beginTime},'%Y-%m-%d %H:%i-%s')
and date_format(l.oper_time,'%Y-%m-%d %H:%i-%s') <= date_format(#{endTime},'%Y-%m-%d %H:%i-%s')
and l.oper_ip = #{operIp}
and (u.create_by = #{createBy} or l.oper_name = #{createBy})
order by oper_id desc
