mapper.select:
select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.status, u.create_time from sys_user u left join sys_tenant t on u.t_id = t.t_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
${params.dataScope}

mapper.update:
update sys_user
t_id = #{tid}, user_name = #{userName}, nick_name = #{nickName}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, google_switch = #{googleSwitch}, update_time = sysdate()
where user_id = #{userId}

mapper.select:
SELECT u.user_id userId ,u.user_name userName ,u.nick_name nickName ,t.t_name tenantName ,u.status ,u.create_time createTime ,u.login_date loginDate ,u.google_key googleKey ,u.google_switch google_switch ,u.t_id tid ,r.role_id roleId ,r.role_name roleName FROM sys_user u LEFT JOIN sys_tenant t ON t.t_id = u.t_id left join sys_user_role ur on ur.user_id = u.user_id left join sys_role r on ur.role_id = r.role_id
AND u.user_name like concat('%', #{userName}, '%')
AND u.user_id = #{userId}
AND u.t_id = #{tid}
AND u.status = #{status}
and u.user_id !=1
order by u.create_time desc
${params.dataScope}
